import ReactFullpage from '@fullpage/react-fullpage';
import { BlogArea } from './Components/Area/BlogArea'

const anchors = ['section1', 'section2', 'section3'];

const Header = ({ goToSection }) => (
  <ul>
    <li onClick={() => goToSection(anchors[0])}>Section 1</li>
    <li onClick={() => goToSection(anchors[1])}>Section 2</li>
    <li onClick={() => goToSection(anchors[2])}>Section 3</li>
  </ul>
);

function App() {
  return (
    <>
      <ReactFullpage
        //fullpage options
        licenseKey={"your licencekey here"}
        scrollingSpeed={1000} 
        navigation
        navigationPosition={'left'}
        anchors={anchors}
        render={({ state, fullpageApi }) => {
          return (
            <ReactFullpage.Wrapper>
              <div className="section">
                <Header goToSection={fullpageApi.moveTo} />
              </div>
              <div className="section">
                <Header goToSection={fullpageApi.moveTo} />
              </div>
              <BlogArea />
            </ReactFullpage.Wrapper>
          );
        }}
      />
    </>
  );
}

export default App;
