import ReactFullpage from '@fullpage/react-fullpage'
import { BlogArea } from './Components/Area/BlogArea'
import { BrowserRouter, Link } from 'react-router-dom';
import styled from 'styled-components';
import { Header } from './Components/Header/Header';
import { useState } from 'react';

function App() {
  const anchors = ['top','about', 'works', 'blog','contact']
  const [fullpageState, setFullpageState] = useState({})

  return (
    <>
    <Header fullpageState={fullpageState} />
      <ReactFullpage
        scrollingSpeed={800} 
        navigation
        navigationPosition={'left'}
        anchors={anchors}
        onLeave={(origin, destination, direction) => {
          setFullpageState({ origin, destination, direction });
        }}
      >
            <>
              <ReactFullpage.Wrapper>
                <div className="section">
                  Top
                </div>
                <div className="section">
                  about
                </div>
                <div className="section">
                  works
                </div>
                <BlogArea />
                <div className="section">
                  contact
                </div>
              </ReactFullpage.Wrapper>
            </>
        </ReactFullpage>
    </>
  );
}

export default App;
const SLinkArea = styled.div`
  position: fixed;
  top: 10%;
  z-index: 10000;
`
