import axios from 'axios'

export const blogDataFetchAll = (setArticles) => {
  const fetchData = async () => {
    const result = await axios(
      'https://k-portfolio-blog.microcms.io/api/v1/blog',

      {
        headers: { 'X-API-KEY': 'jIXPaJk5rZRRrDcQfPLhurUxPx3M8G7wLIAM' }
      }
    );
    setArticles(result.data.contents)
  };
  fetchData()
}

export const fetchBlogById = async (id) => {
  const result = await axios.get(
    `https://k-portfolio-blog.microcms.io/api/v1/blog/${id}`,
    {
      headers: { 'X-API-KEY': 'jIXPaJk5rZRRrDcQfPLhurUxPx3M8G7wLIAM' }
    }
  );
  return result.data;
};
