import ReactFullpage from '@fullpage/react-fullpage';
import { BlogArea } from './Components/Area/BlogArea'
import { useRef } from 'react';

function App() {
  const menuItems = ['page1', 'page2', 'BlogArea'];
  const handleMenuItemClick = (page) => {
    fullpageApi.moveTo(page);
  };
  let fullpageApi;

  function Menu({ menuItems, handleMenuItemClick }) {
  return (
    <nav>
      <ul>
        {menuItems.map((menuItem) => (
          <li key={menuItem.id} onClick={() => handleMenuItemClick(menuItem)}>
            {menuItem.label}
          </li>
        ))}
      </ul>
    </nav>
  );
}

  return (
    <>
      <ReactFullpage
        //fullpage options
        licenseKey={"your licencekey here"}
        scrollingSpeed={1000} 
        navigation
        navigationPosition={'left'}
        afterRender={(fullpage) => {
          fullpageApi = fullpage;
        }}
        render={({ state, fullpageApi }) => {
          return (
            <ReactFullpage.Wrapper>
              <div className="section" data-anchor="page1">page1</div>
              <div className="section" data-anchor="page2">page2</div>
              <BlogArea />
            </ReactFullpage.Wrapper>
          );
        }}
      />
      <Menu menuItems={menuItems} handleMenuItemClick={handleMenuItemClick} />
    </>
  );
}

export default App;
