import ReactFullpage from '@fullpage/react-fullpage';
import { BlogArea } from './Components/Area/BlogArea'
import { useRef } from 'react';
useRef
function App() {
  const fullpageApi = React.useRef(null);

  // ヘッダー用のリンクの配列
  const menuLinks = [
    {
      text: "Section 1",
      section: 0,
    },
    {
      text: "Section 2",
      section: 1,
    },
    {
      text: "Blog",
      section: 2,
    },
  ];

  const handleMenuClick = (sectionIndex) => {
    fullpageApi.current.moveTo(sectionIndex + 1);
  };

  return (
    <>
      {/* ヘッダー */}
      <nav className="header">
        {menuLinks.map((link, index) => (
          <a key={index} onClick={() => handleMenuClick(link.section)}>
            {link.text}
          </a>
        ))}
      </nav>

      {/* ReactFullpage */}
      <ReactFullpage
        //fullpage options
        licenseKey={"your licencekey here"}
        scrollingSpeed={1000} 
        navigation
        navigationPosition={'left'}
        render={({ state, fullpageApi }) => {
          // ReactFullpageのAPIを保存
          fullpageApi.current = fullpageApi;

          return (
            <ReactFullpage.Wrapper>
              <div className="section">Section 1</div>
              <div className="section">Section 2</div>
              <BlogArea />
            </ReactFullpage.Wrapper>
          );
        }}
      />
    </>
  );
}

export default App;
