import ReactFullpage from '@fullpage/react-fullpage'
import { BlogArea } from './Components/Area/BlogArea'
import { BrowserRouter, Link } from 'react-router-dom';

function App() {
  const anchors = ["section1", "section2", "blog"];
  return (
    <>
      <ReactFullpage
        scrollingSpeed={800} 
        navigation
        navigationPosition={'left'}
        anchors={anchors}
        render={({ state, fullpageApi }) => {
          return (
            <>
            <BrowserRouter>
              <ReactFullpage.Wrapper>
                <div className="section">
                <Link to='/#section1'>page1</Link>
                <Link to='/#section2'>page2</Link>
                <Link to='#blog'>Blog</Link>
                  page1
                </div>
                <div className="section">
                <a href='#section1'>page1</a>
                <a href='#section2'>page2</a>
                <a href='#blog'>Blog</a>
                  page2
                </div>
                <BlogArea />
              </ReactFullpage.Wrapper>
            </BrowserRouter>
            </>
          );
        }}
      />
    </>
  );
}

export default App;
