import ReactFullpage from '@fullpage/react-fullpage';
import { BlogArea } from './Components/Area/BlogArea'

function App() {
  const fullpageApiRef = useRef(null);

  useEffect(() => {
    if (fullpageApiRef.current) {
      fullpageApiRef.current.moveTo(1);
    }
  }, []);

  const handleMenuItemClick = (anchor) => {
    if (fullpageApiRef.current) {
      fullpageApiRef.current.moveTo(anchor);
    }
  };

  return (
    <>
      <Menu menuItems={menuItems} handleMenuItemClick={handleMenuItemClick} />
      <ReactFullpage
        //fullpage options
        licenseKey={"your licencekey here"}
        scrollingSpeed={1000}
        navigation
        navigationPosition={'left'}
        afterRender={() => {
          if (fullpageApiRef.current) {
            fullpageApiRef.current.destroy('all');
            fullpageApiRef.current.reBuild();
          }
        }}
        render={({ state, fullpageApi }) => {
          fullpageApiRef.current = fullpageApi;
          return (
            <>
              <ReactFullpage.Wrapper>
                <div className="section" data-anchor="page1">page1</div>
                <div className="section" data-anchor="page2">page2</div>
                <BlogArea id='blog' />
              </ReactFullpage.Wrapper>
            </>
          );
        }}
      />
    </>
  );
}

export default App;
