import styled from "styled-components"
import { HeaderMenu } from "../../Config/MenuConfig"
import { useState, useEffect } from "react";

export const Header = ({ state }) => {
  const [activeMenuIndex, setActiveMenuIndex] = useState(0);
  const anchors = ['top','about', 'works', 'blog','contact'];

  useEffect(() => {
    // stateオブジェクトから現在アクティブなセクションのインデックスを取得
    const activeSectionIndex = state && state.activeSection - 1;

    // HeaderMenuのリンクと現在のセクションを比較して、アクティブなリンクのインデックスをセット
    HeaderMenu.forEach((menu, index) => {
      if (menu.link === `/#${anchors[activeSectionIndex]}`) {
        setActiveMenuIndex(index);
      }
    });
  }, [state]);

  return (
    <SHeaderNav>
      <div>
        <STopLink href="/#top">KYOSUKE OCHIAI</STopLink>
      </div>
      <div>
        {HeaderMenu.map((menu, index) => (
          <SNavLink
            key={menu.index}
            href={menu.link}
            className={index === activeMenuIndex ? "active" : ""}
          >
            {menu.name}
          </SNavLink>
        ))}
      </div>
    </SHeaderNav>
  );
}

const SHeaderNav = styled.nav`
  position: fixed;
  width: 100%;
  display: flex;
  justify-content: space-between;
  padding: 1em 2em;
  font-size: 1.5em;
  top: 0;
  z-index: 10000;
`
const STopLink = styled.a`
  color: #fff;
`
const SNavLink = styled.a`
  color: #fff;
  margin-left: 1em;
`
