          // 現在表示されているセクションのアンカー名を取得する
          const activeSection = destination.anchor;
          // Headerコンポーネントに渡す
          setSectionName(activeSection);
          return (
            <>
              <ReactFullpage.Wrapper>
                <div className="section">
                  Top
                </div>
                <div className="section">
                  about
                </div>
                <div className="section">
                  works
                </div>
                <BlogArea />
                <div className="section">
                  contact
                </div>
              </ReactFullpage.Wrapper>
            </>
          );
        }}
      />
    </>
  );
}

export default App;
const SLinkArea = styled.div`
  position: fixed;
  top: 10%;
  z-index: 10000;
`
